mapper.select:
SELECT COUNT(*) CNT
  FROM MEMBER
 WHERE ID = #{id}

SELECT COUNT(*) CNT
  FROM MEMBER
 WHERE ID = #{id}
   AND PASS = #{oriPass}

SELECT ID , CASE WHEN TYPE = '01' THEN CONCAT('+',POINT) ELSE CONCAT('-',POINT) END POINT , MESSAGE , FROM_ID , TO_ID , DATE_FORMAT(REGDATE,'%Y-%m-%d %H:%i:%s') REGDATE FROM POINT_HISTORY WHERE 1 = 1
AND REGDATE BETWEEN #{searchFromDate} AND #{searchToDate}
ORDER BY REGDATE DESC LIMIT #{beginIdx} , #{searchRows}

mapper.insert:
INSERT
  INTO MEMBER
       ( ID , PASS , NAME , PHONE
       , ZIPCODE , ADDR1 , ADDR2 , POINT
       , DELIVERY_ZIPCODE , DELIVERY_ADDR1 , DELIVERY_ADDR2
       , RECOMMENDER , SPONSOR , AGENT, GRADE
       , REGDATE , REGID
       )
VALUES ( LOWER(#{id}) , #{pass} , #{name} , #{phone}
       , #{zipcode} , #{addr1} , #{addr2} , 0
       , #{deliveryZipcode} , #{deliveryAddr1} , #{deliveryAddr2}
       , #{recommender} , #{sponsor} , #{agent} , #{grade}
       , NOW() , #{regid}
       )

INSERT
  INTO POINT_HISTORY
       ( ID , MESSAGE , POINT
       , TYPE , REGDATE
       )
VALUES ( #{id} , #{message} , #{point}
       , #{type} , NOW()
       )

mapper.update:
UPDATE MEMBER
   SET NAME = #{name}
     , PHONE = #{phone}
     , PASS = #{pass}
     , ZIPCODE = #{zipcode}
     , ADDR1 = #{addr1}
     , ADDR2 = #{addr2}
     , MODID = #{modid}
     , MODDATE = NOW()
 WHERE ID = #{id}

UPDATE MEMBER
   SET POINT = POINT + #{point}
     , MODID = #{id}
     , MODDATE = NOW()
 WHERE ID = #{id}
